INSERT INTO NOTIFICATION_TYPE  (NOTIFICATION_TYPE_ID,MODULE_CODE,SUB_MODULE_CODE,DESCRIPTION,SUBJECT,MESSAGE,PROMPT_USER,IS_ACTIVE,CREATE_USER,CREATE_TIMESTAMP,UPDATE_USER,UPDATE_TIMESTAMP,IS_SYSTEM_SPECIFIC) VALUES (764,22,0,'PRE_PROPOSAL - Add Reviewer','KU- External Reviewer allocation request for:  {PROPOSAL_TITLE} - {REVIEW_ID} {REVIEW_TITLE} ','<p>Dear Dr. {REVIEWER_NAME},</p><p><br><span style=\"color:black;\">Thank you for confirming you are willing to undertake proposal evaluation assignments for Khalifa Universitys competitive internal research programs.</span></p><p><span style=color:black;>Based on the abstracts received for KUs current active funding program, we have identified the following project that we consider you may be suitable to review:</span><br><br>Proposal Title: {PROPOSAL_TITLE}</p><p>Principal Investigator: {PRINCIPAL_INVESTIGATOR}<br>Deadline Date: {REVIEWER_DEADLINE_DATE}</p><p>Review Ref: <i>{REVIEW_ID} {REVIEW_TITLE}</i><br> </p><p><strong>Please follow {APPLICATION_URL} to access the abstract.</strong> <span style=color:black;>The documents in the system are to be considered </span><span style=color:hsl(0,75%,60%);><i><strong><u>confidential.</u></strong></i></span><span style=color:black;> These preliminary informational items are not for formal evaluation and feedback, rather they are shared to help you confirm the suitability of your expertise to assess the proposal subject matter, identify any conflicts of interest, and affirm your ability to complete assignment by deadline if this allocation is accepted. Full proposals for your accepted allocation(s) will be shared with you thereafter to conduct formal evaluation (scoring and technical feedback).</span><br><br><span style=color:black;>We kindly ask that you consider this request and complete your response in the system within 5 calendar days. If you have any questions about this allocation request, you may enter it as a comment in the system and our team will respond accordingly. </span><br><br><span style=\"color:black;\">Thank you in advance, </span><br><br><span style=\"color:black;\"><i>Maleka Bin Tarsh</i></span><br><span style=\"color:black;\"><i>Reviewer Engagement Specialist</i></span><br><span style=\"color:black;\"><i>University Sponsored Research</i></span><br><span style=color:black;><i>Khalifa University</i></span></p>','N','Y','quickstart','2022-10-13 19:30:10','fibi_admin',NOW(),'N');
